.header {
    display: flex;
    justify-content: space-between;
    align-items: center;
    box-sizing: border-box;
    /* border-box对元素指定宽度width和高度height包括了 padding 和 border */
    width: 100%;
    height: 70px;
    font-size: 22px;
    /* 此处控制的为icon和字体的颜色，而非header的bgc */
    color: #fff;
    background-color: rgb(230, 162, 60);
}

.nothing {
    float: left;
    padding: 0 21px;
    line-height: 70px;
}

/* 侧边栏折叠按钮样式 */
.collapse-btn {
    float: left;
    padding: 0 21px;
    /* 悬浮时的鼠标图标为手指 */
    /* cursor: pointer; */
    line-height: 70px;
}

.lidarPic {
    width: 50px;
    height: 50px;
    text-align: center;
    margin-top: 8px;
}

.header .logo {
    font-size: 40px;
    float: left;
    width: 220px;
    line-height: 70px;
}

.header-left {
    display: flex;
    justify-content: flex-start;
    text-align: center;
}

.header-right {
    float: right;
    padding-right: 50px;
}
.header-user-con {
    display: flex;
    height: 70px;
    align-items: center;
}
.btn-fullscreen {
    transform: rotate(45deg);
    margin-right: 5px;
    font-size: 24px;
}
.btn-bell,
.btn-fullscreen {
    position: relative;
    width: 30px;
    height: 30px;
    text-align: center;
    border-radius: 15px;
    cursor: pointer;
}
.btn-bell-badge {
    position: absolute;
    right: 0;
    top: -2px;
    width: 8px;
    height: 8px;
    border-radius: 4px;
    background: #f56c6c;
    color: #fff;
}
.btn-bell .el-icon-bell {
    color: #fff;
}
.user-name {
    margin-left: 10px;
}
.user-avator {
    margin-left: 20px;
}
.user-avator img {
    display: block;
    width: 40px;
    height: 40px;
    border-radius: 50%;
}
.el-dropdown-link {
    color: #fff;
    cursor: pointer;
}
.el-dropdown-menu__item {
    text-align: center;
}
