        };
    },
    destroyed() {
        clearInterval(this.timer);
    },
    mounted() {
        this.getECharts();
        this.timer = setInterval(() => {
            this.getECharts();
        }, 1000 * 60);
    },
    methods: {
        handleClick() {
            this.getTwo();
        },
        getECharts() {
            this.getTwo();
        },
        async getTwo() {
            const { data: data } = await this.$http.get('log/recent_fifteen_days/');
            if (data.code !== 20000) {
                return this.$message.error({
                    showClose: true,
                    message: '获取近15日系统日志数据失败！' + '错误原因：' + data.msg,
                    offset: 70
                });
            }
            this.logData = data.data;
            // console.log('this.logData:', this.logData);
            this.showData();
        },
        showData() {
            this.dates = [];
            this.methods = [];
            this.datas_ran = [];
            this.datas_finish = [];
            this.datas_generate = [];
            this.temp_series_ran = [];
            this.temp_series_finish = [];
            this.temp_series_generate = [];
            var flag = true;
            for (var date in this.logData) {
                this.dates.push(date);
                for (var method in this.logData[date]) {
                    if (flag) {
                        this.methods.push(method);
                    }
                }
                flag = false;
            }

            for (var method in this.methods) {
                var temp_ran = [];
                var temp_finish = [];
                var temp_generate = [];
                for (var date in this.dates) {
                    temp_ran.push(this.logData[this.dates[date]][this.methods[method]]['ran_task_number']);
                    temp_finish.push(this.logData[this.dates[date]][this.methods[method]]['finish_task_number']);
                    temp_generate.push(this.logData[this.dates[date]][this.methods[method]]['generate_data_number']);
                }
                this.datas_ran.push(temp_ran);
                this.datas_finish.push(temp_finish);
                this.datas_generate.push(temp_generate);
            }

            for (var i = 0; i < this.methods.length; i++) {
                var getone = {};
                getone.type = 'line';
                getone.areaStyle = {};
                getone.name = this.methods[i];
                getone.data = this.datas_ran[i].reverse();
                this.temp_series_ran.push(getone);
            }
            this.methods_temp = [];
            console.log('this.methods:', this.methods);
            // this.methods_temp = [];
            this.methods_temp.push(this.methods[9], this.methods[11]);
            console.log('this.methods_temp:', this.methods_temp);

            var temp_data_ran = [];
            temp_data_ran.push(this.temp_series_ran[9], this.temp_series_ran[11]);
            console.log('temp_data_ran:', temp_data_ran);
            if (myChart != null && myChart != '' && myChart != undefined) {
                myChart.dispose(); //销毁
            }
            myChart = echarts.init(document.getElementById('main1'));
            var option = {
                title: {
                    text: 'Running Tasks',
                    left: 'center'
                },
                tooltip: {
                    trigger: 'axis',
                    axisPointer: {
                        type: 'cross',
                        label: {
                            backgroundColor: '#6a7985'
                        }
                    }
                },
                legend: {
                    data: ['场景物体添加蜕变-激光雷达', '极端场景蜕变-激光雷达'],
                    top: '10%'
                },
                toolbox: {
                    feature: {
                        saveAsImage: {},
                        magicType: {
                            type: ['stack']
                        }
                    }
                },
                grid: {
                    left: '3%',
                    right: '4%',
                    bottom: '3%',
                    containLabel: true
                },
                xAxis: [
                    {
                        type: 'category',
                        boundaryGap: true,
                        data: this.dates.reverse()
                    }
                ],
                yAxis: [
                    {
                        type: 'value'
                    }
                ],
                series: temp_data_ran
            };
            if (option && typeof option === 'object') {
                myChart.setOption(option, true);
            }
            for (var i = 0; i < this.methods.length; i++) {
                var getone = {};
                getone.type = 'line';
                getone.areaStyle = {};
                getone.name = this.methods[i];
                getone.data = this.datas_finish[i].reverse();
                this.temp_series_finish.push(getone);
            }
            var temp_data_finish = [];
            temp_data_finish.push(this.temp_series_finish[9], this.temp_series_finish[11]);
            console.log('temp_data_finish:', temp_data_finish);
            if (myChart2 != null && myChart2 != '' && myChart2 != undefined) {
                myChart2.dispose();
            }
            myChart2 = echarts.init(document.getElementById('main2'));
            var option2 = {
                title: {
                    text: 'Completed Tasks',
                    left: 'center'
                },
                tooltip: {
                    trigger: 'axis',
                    axisPointer: {
                        type: 'cross',
                        label: {
                            backgroundColor: '#6a7985'
                        }
                    }
                },
                legend: {
                    data: ['场景物体添加蜕变-激光雷达', '极端场景蜕变-激光雷达'],
                    top: '8%'
                },
                toolbox: {
                    feature: {
                        saveAsImage: {},
                        magicType: {
                            type: ['stack']
                        }
                    }
                },
                grid: {
                    left: '3%',
                    right: '4%',
                    bottom: '3%',
                    containLabel: true
                },
                xAxis: [
                    {
                        type: 'category',
                        boundaryGap: true,
                        data: this.dates
                    }
                ],
                yAxis: [
                    {
                        type: 'value'
                    }
                ],
                series: temp_data_finish
            };
            if (option2 && typeof option2 === 'object') {
                myChart2.setOption(option2, true);
            }
            for (var i = 0; i < this.methods.length; i++) {
                var getone = {};
                getone.type = 'line';
                getone.areaStyle = {};
                getone.name = this.methods[i];
                getone.data = this.datas_generate[i].reverse();
                this.temp_series_generate.push(getone);
            }
            var temp_data_generate = [];
            temp_data_generate.push(this.temp_series_generate[9], this.temp_series_generate[11]);
            console.log('temp_data_generate:', temp_data_generate);

            if (myChart3 != null && myChart3 != '' && myChart3 != undefined) {
                myChart3.dispose();
            }
            myChart3 = echarts.init(document.getElementById('main3'));
            var option3 = {
                title: {
                    text: 'Generated Data',
                    left: 'center'
                },
                tooltip: {
                    trigger: 'axis',
                    axisPointer: {
                        type: 'cross',
                        label: {
                            backgroundColor: '#6a7985'
                        }
                    }
                },
                legend: {
                    data: ['场景物体添加蜕变-激光雷达', '极端场景蜕变-激光雷达'],
                    top: '8%'
                },
                toolbox: {
                    feature: {
                        saveAsImage: {},
                        magicType: {
                            type: ['stack']
                        }
                    }
                },
                grid: {
                    left: '3%',
                    right: '4%',
                    bottom: '3%',
                    containLabel: true
                },
                xAxis: [
                    {
                        type: 'category',
                        boundaryGap: true,
                        data: this.dates
                    }
                ],
                yAxis: [
                    {
                        type: 'value'
                    }
                ],
                series: temp_data_generate
            };
            if (option3 && typeof option3 === 'object') {
                myChart3.setOption(option3, true);
            }
        }
    }
